import { ComponentEvent } from "./events";

export type LocalGitRepoState =
  | { status: "NO_GIT" }
  | { status: "DETACHED" }
  | {
      status: "IN_A_BRANCH";
      localBranchName: string;
      upstream?: {
        remoteName: string;
        url: string;
        pushUrl?: string | null;
        branchName: string;
      };
    };

export interface ValidateGitSetupRequestBody {
  gitState: LocalGitRepoState;
  event?: ComponentEvent;
}
